mapper.select:
SELECT building_id, name, latitude, longitude
  FROM building
 WHERE building_id = #{buildingId}

mapper.update:
UPDATE building
   SET name = #{name},
       latitude = #{latitude},
       longitude = #{longitude}
 WHERE building_id = #{buildingId};

mapper.select:
SELECT building_id, name, latitude, longitude
  FROM building
 WHERE name = #{name}
